/**
 *
 * @param {number} authUserId - The authenticated user Id
 * @param {number} channelId - The channel Id to join
 * ...
 *
 * @returns {} - returns {} when successful
 * @returns {error : 'error message'} - returns an error when
 *                                    | channelId is invalid
 *                                    | Member is already in channel
 *                                    | Channel is private and not a global owner
 *                                    | User is invalid
 */
export function channelMessagesV1(
  token: string,
  channelId: number,
  start: number
): messages | errorMessage {
  const user = getUserByToken(token);

  const channel = findChannel(channelId);

  const allMemberIds = getAllMemberIds(channel);

  if (user === undefined) {
    throw HTTPError(403, 'Invalid user token');
  }

  if (channel === undefined) {
    throw HTTPError(400, 'Channel Not Found');
  }
  const channelMessage = channel.messages.length;
  const uId = user.authUserId;
  // Error if the user is not a member of the channel
  if (allMemberIds.includes(uId) === false) {
    throw HTTPError(403, 'User is not a member of the channel');
  }
  // Error if 'start' is greater than the amount of messages
  if (start > channelMessage) {
    throw HTTPError(400, '"Start" is greater than the amount of messages');
  }

  for (const message of channel.messages) {
    if (message.reacts.length !== 0) {
      if (message.reacts[0].uIds.includes(user.authUserId)) {
        message.reacts[0].isThisUserReacted = true;
      } else {
        message.reacts[0].isThisUserReacted = false;
      }
    }
  }
  // reverse message
  channel.messages.reverse();

  if (start + 50 > channelMessage) {
    return {
      messages: channel.messages.slice(start),
      start: start,
      end: -1,
    };
  } else {
    return {
      messages: channel.messages.slice(start, start + 50),
      start: start,
      end: start + 50,
    };
  }
}

/**
 * Given a channelId of a channel that the authorised user
 * can join, adds them to the channel.
 *
 * @param {string} token - The authenticated token
 * @param {number} channelId - The channel Id to join
 * ...
 *
 * @returns {} - returns {} when successful
 * @returns {error : 'error message'} - returns an error when
 *                                    | channelId is invalid
 *                                    | Member is already in channel
 *                                    | Channel is private and not a global owner
 *                                    | User token is invalid
 */

export function channelJoinV1(
  token: string,
  channelId: number
): object | errorMessage {
  const data = getData();
  // Get the particular user in data store
  const user = getUserByToken(token);

  // Get the particular channel index from data store
  const channel = findChannel(channelId);

  const allMemberIds = getAllMemberIds(channel);

  if (user === undefined) {
    throw HTTPError(403, 'Invalid user token');
  }

  if (channel === undefined) {
    throw HTTPError(400, 'Channel Not Found');
  }

  const uId = user.authUserId;

  if (allMemberIds.includes(uId) === true) {
    throw HTTPError(400, 'User is already in channel');
  }

  // channel
  if (channel.isPublic === false && user.isGlobalOwner === 2) {
    throw HTTPError(403, 'Channel is not public');
  }
  const channelNum = getChannelIndex(channelId);

  data.channels[channelNum].allMembers.push({
    uId: user.authUserId,
    email: user.email,
    nameFirst: user.nameFirst,
    nameLast: user.nameLast,
    handleStr: user.handleStr,
    profileImgUrl: user.profileImgUrl,
  });

  setData(data);

  // updates user stats
  updateChannelInfo(user.authUserId, 0);

  return {};
}
/**
 *
 * Invites a user to a channel
 * @param {string} token - The authenticated user token
 * @param {number} channelId - The channel Id to join
 * @param {number} uId - The user Id of the invited member
 *
 * @returns {error : 'error message'} - returns an error when
 *                                    | channelId is invalid
 *  *                                 | uId is invalid
 *                                    | User is already in the channel
 *  *                                 | Token refers to a member not in the channel
 *                                    | token is invalid
 */
export function channelInviteV1(token: string, channelId: number, uId: number) {
  const data = getData();

  // Error cases
  const user = getUserByToken(token);
  if (user === undefined) {
    throw HTTPError(403, 'Invalid token');
  }
  if (!isChannel(channelId)) {
    throw HTTPError(400, 'channelId does not refer to a valid channel');
  }
  if (!isUser(uId)) {
    throw HTTPError(400, 'uId does not refer to a valid user');
  }
  const channel = findChannel(channelId);

  // Get all uIds in the channel
  const allMemberIds = getAllMemberIds(channel);

  if (allMemberIds.includes(uId) === true) {
    throw HTTPError(400, 'User already in the channel');
  }

  if (
    isChannel(channelId) &&
    allMemberIds.includes(user.authUserId) === false
  ) {
    throw HTTPError(403, 'You are not a channel member');
  }
  // Finds the user based on uId
  const invitedUser = findUser(uId);
  const channelNum = getChannelIndex(channelId);

  // Adds the user to the channel
  data.channels[channelNum].allMembers.push({
    uId: invitedUser.authUserId,
    email: invitedUser.email,
    nameFirst: invitedUser.nameFirst,
    nameLast: invitedUser.nameLast,
    handleStr: invitedUser.handleStr,
    profileImgUrl: invitedUser.profileImgUrl,
  });

  // if user is invited send notification
  const notification = {
    channelId: channelId,
    dmId: -1,
    notificationMessage: `${user.handleStr} added you to ${channel.name}`,
  };

  const userInvitedIndex = findUserIndex(uId);
  data.users[userInvitedIndex].notifications.push(notification);

  setData(data);

  // update invited user stats
  updateChannelInfo(uId, 0);
  return {};
}

/**
 *
 * Shows the details of the given channel
 * @param {string} token - The authenticated user token
 * @param {number} channelId - The channel Id to join
 *
 * @returns {
 *  name: string,
 *  isPublic: boolean,
 *  ownerMembers: string,
 *  allMembers: string} - returns the details of the channel when successful
 *
 * @returns {error : 'error message'} - returns an error when
 *                                    | channelId is invalid
 *                                    | User is not a member of the channel
 *                                    | User is invalid
 */
export function channelDetailsV1(token: string, channelId: number) {
  // If channelId doesn't refer to a valid channel,
  // returns error
  // console.log(token);
  // console.log(channelId);
  const user = getUserByToken(token);
  if (!isChannel(channelId)) {
    throw HTTPError(400, 'channelId does not refer to a valid channel');
  } else if (user === undefined) {
    // If token is invalid, returns error
    throw HTTPError(403, 'Invalid token');
  }
  // If the user is not a member of the channel
  if (!isChannelMember(channelId, user.authUserId)) {
    throw HTTPError(403, 'User is not a member of the channel');
  }
  const channelObj = findChannel(channelId);
  return {
    name: channelObj.name,
    isPublic: channelObj.isPublic,
    ownerMembers: channelObj.ownerMembers,
    allMembers: channelObj.allMembers,
  };
}

/**
 * Given a channelId of a channel and token, removing the membership of that member,
 * but remain the information of the authorised user
 *
 * @param {string} token - The authenticated token
 * @param {number} channelId - The channel Id to join
 * ...
 *
 * @returns {} - returns {} when successful
 * @returns {error : 'error message'} - returns an error when
 *                                    | channelId is invalid
 *                                    | user is not the channel member
 *                                    | User token is invalid
 */
export function channelLeaveV1(token: string, channelId: number) {
  const data = getData();
  const user = getUserByToken(token);
  if (!isChannel(channelId)) {
    throw HTTPError(400, 'channelId does not refer to a valid channel');
  }
  if (user === undefined) {
    throw HTTPError(403, 'Invalid token');
  }

  if (!isChannelMember(channelId, user.authUserId)) {
    throw HTTPError(403, user.authUserId + ' is not a member of the channel');
  }

  // if standup active and user is standup, throw error
  const channel = findChannel(channelId);
  const standupActive = standupActiveV1(token, channelId).isActive;
  const standupUser = channel.standUp.standUpOwner;
  if (standupActive && standupUser === user.authUserId) {
    throw HTTPError(400, 'Cannot leave channel while standup is active');
  }

  const channelIndex = data.channels.findIndex(
    (item) => item.channelId === channelId
  );
  const userOwnerIndex = data.channels[channelIndex].ownerMembers.findIndex(
    (item) => item.uId === user.authUserId
  );
  const userMemberIndex = data.channels[channelIndex].allMembers.findIndex(
    (item) => item.uId === user.authUserId
  );
  if (userOwnerIndex !== -1) {
    data.channels[channelIndex].ownerMembers.splice(userOwnerIndex, 1);
  }

  data.channels[channelIndex].allMembers.splice(userMemberIndex, 1);

  setData(data);

  // update user stats
  updateChannelInfo(user.authUserId, 1);
  return {};
}
/**
 *
 * @param {string} token - Token of the user performing the action
 * @param {number} channelId - ID of the channel where the user will be added as owner
 * @param {number} uId - ID of the user to be added as owner
 * @returns {Object | errorMessage} - Empty object if successful, error object otherwise
 */
export function channelAddOwnerV1(
  token: string,
  channelId: number,
  uId: number
): object | errorMessage {
  const data = getData();
  const user = getUserByToken(token);
  const uIdfound = findUser(uId);

  // Error checking
  if (!isChannel(channelId)) {
    throw HTTPError(400, 'channelId does not refer to a valid channel');
  }

  if (uIdfound === undefined) {
    throw HTTPError(400, 'Invalid uId');
  }

  if (user === undefined) {
    throw HTTPError(403, 'Invalid token');
  }

  if (!isChannelMember(channelId, uId)) {
    throw HTTPError(400, 'User is not a member of the channel');
  }

  if (!isChannelMember(channelId, user.authUserId)) {
    throw HTTPError(403, 'You are not a member of the channel');
  }

  if (isChannelOwner(uId, channelId)) {
    throw HTTPError(400, user.authUserId + ' is already owner of this channel');
  }

  if (!isChannelOwner(user.authUserId, channelId) && user.isGlobalOwner === 2) {
    throw HTTPError(403, user.authUserId + ' has no owner permission');
  }

  // owner adds the user with uId to the ownermembers and allmembers of the channel
  const channelfound = data.channels.find((a) => a.channelId === channelId);
  channelfound.ownerMembers.push({
    uId: uIdfound.authUserId,
    email: uIdfound.email,
    nameFirst: uIdfound.nameFirst,
    nameLast: uIdfound.nameLast,
    handleStr: uIdfound.handleStr,
    profileImgUrl: uIdfound.profileImgUrl,
  });
  setData(data);
  return {};
}

/**
 *
 * @param {string} token - The user's authentication token.
 * @param {number} channelId - The ID of the channel.
 * @param {number} uId - The ID of the user to remove as owner.
 * @returns {Object | errorMessage} An empty object if successful, or an object with an "error" property if unsuccessful.
 */
export function channelRemoveOwnerV1(
  token: string,
  channelId: number,
  uId: number
): object | errorMessage {
  const data = getData();
  const user = getUserByToken(token);
  const uIdfound = findUser(uId);
  const channelfound = data.channels.find((a) => a.channelId === channelId);

  if (!isChannel(channelId)) {
    throw HTTPError(400, 'channelId does not refer to a valid channel');
  }
  // invalid uId
  if (uIdfound === undefined) {
    throw HTTPError(400, 'Invalid uId');
  }
  // invalid token
  if (user === undefined) {
    throw HTTPError(403, 'Invalid token');
  }

  // Check if user is in the channel
  if (!isChannelMember(channelId, uId)) {
    throw HTTPError(400, ' is not a member of the channel');
  }

  // Check if the user executing the command is in the channel
  if (!isChannelMember(channelId, user.authUserId)) {
    throw HTTPError(403, 'You are not a member of the channel');
  }

  // uId user is not owner of the channel
  if (!isChannelOwner(uId, channelId)) {
    throw HTTPError(400, user.authUserId + ' is not owner of this channel');
  }
  // user with token is neither channel owner nor the global owner
  // global owner if 1
  if (!isChannelOwner(user.authUserId, channelId) && user.isGlobalOwner !== 1) {
    throw HTTPError(403, user.authUserId + ' has no owner permission');
  }
  // the owner is the only one in the channel
  if (channelfound.ownerMembers.length === 1) {
    throw HTTPError(400, user.authUserId + ' is the only owner');
  }

  // owner removes the other owner with uId from channel
  const ownerIndex = findOwnerIndex(channelId, uIdfound.authUserId);

  channelfound.ownerMembers.splice(ownerIndex, 1);
  setData(data);
  return {};
}

import { getData, setData } from './dataStore';
import HTTPError from 'http-errors';
import {
  findChannel,
  findUser,
  getAllMemberIds,
  getChannelIndex,
  getUserByToken,
  incrementMessageStat,
} from './functionHelper';

/**
 *
 * @param token - token of user starting standup
 * @param channelId - channel id of channel to start standup in
 * @param length - length of standup in seconds
 * @returns {timeFinish: number} - timeFinish is the time the standup will finish
 */
export function standupStartV1(
  token: string,
  channelId: number,
  length: number
) {
  const user = getUserByToken(token);
  const channel = findChannel(channelId);
  const allMemberIds = getAllMemberIds(channel);

  // Error Checking
  if (user === undefined) {
    throw HTTPError(403, 'Token is invalid');
  }
  if (channel === undefined) {
    throw HTTPError(400, 'Channel does not exist');
  }
  if (allMemberIds.includes(user.authUserId) === false) {
    throw HTTPError(403, 'User is not registered in channel');
  }
  if (length < 0) {
    throw HTTPError(400, 'length should be a positive integer');
  }
  const standUpDeets = standupActiveV1(token, channelId);
  if (standUpDeets.isActive) {
    throw HTTPError(400, 'Standup is already active');
  }

  const timeFinish = Math.floor(Date.now() / 1000) + length;

  const channelIndex = getChannelIndex(channelId);
  const data = getData();
  data.channels[channelIndex].standUp.standUpActive = true;
  data.channels[channelIndex].standUp.standUpLength = timeFinish;
  data.channels[channelIndex].standUp.standUpOwner = user.authUserId;

  setData(data);

  // timeout
  setTimeout(() => {
    // if token is invalid, do nothing
    try {
      standUpEnd(token, channelId);
    } catch (e) {
      const data = getData();
      const channelIndex = getChannelIndex(channelId);
      const standUp = data.channels[channelIndex].standUp;
      standUp.standUpActive = false;
      standUp.standUpLength = 0;
      standUp.standUpMessage = [];
      standUp.standUpOwner = -1;
      setData(data);
    }
  }, length * 1000);

  return { timeFinish: timeFinish };
}

/**
 *
 * @param token - token of user making request
 * @param channelId - channel id of channel to check standup in
 * @returns {isActive: boolean, timeFinish: number} - isActive is true if standup is active, false otherwise,
 *                                                    timeFinish is the time the standup will finish
 */
export function standupActiveV1(token: string, channelId: number) {
  const user = getUserByToken(token);
  const channel = findChannel(channelId);
  const allMemberIds = getAllMemberIds(channel);

  // Error Checking
  if (user === undefined) {
    throw HTTPError(403, 'Token is invalid');
  }
  if (channel === undefined) {
    throw HTTPError(400, 'Channel does not exist');
  }
  if (allMemberIds.includes(user.authUserId) === false) {
    throw HTTPError(403, 'User is not registered in channel');
  }

  const channelIndex = getChannelIndex(channelId);
  const data = getData();
  const standUp = data.channels[channelIndex].standUp;

  return {
    isActive: standUp.standUpActive,
    timeFinish: standUp.standUpLength,
  };
}

/**
 *
 * @param token - token of user sending message
 * @param channelId - channel id of channel to send message in
 * @param message - message to send
 * @returns {}
 */
export function standupSendV1(
  token: string,
  channelId: number,
  message: string
) {
  const user = getUserByToken(token);
  const channel = findChannel(channelId);
  const allMemberIds = getAllMemberIds(channel);

  // Error Checking
  if (user === undefined) {
    throw HTTPError(403, 'Token is invalid');
  }
  if (channel === undefined) {
    throw HTTPError(400, 'Channel does not exist');
  }
  if (allMemberIds.includes(user.authUserId) === false) {
    throw HTTPError(403, 'User is not registered in channel');
  }
  if (message.length < 1) {
    throw HTTPError(400, 'Message is too short');
  }
  if (message.length > 1000) {
    throw HTTPError(400, 'Message is too long');
  }

  const channelIndex = getChannelIndex(channelId);
  const data = getData();

  const standupDeets = standupActiveV1(token, channelId);

  const timeNow = Math.floor(Date.now() / 1000);
  if (timeNow > standupDeets.timeFinish && standupDeets.isActive === false) {
    throw HTTPError(400, 'Standup has finished');
  }

  const newMessage = {
    messageId: Math.floor(Math.random() * 1000000),
    uId: user.authUserId,
    message: message,
    timeSent: timeNow,
    isPinned: false,
    reacts: [
      {
        reactId: 1,
        uIds: [] as number[],
        isThisUserReacted: false,
      },
    ],
  };

  data.channels[channelIndex].standUp.standUpMessage.push(newMessage);
  setData(data);

  return {};
}

/**
 *
 * @param token - token of user
 * @param channelId - id of channel
 * @returns
 */
function standUpEnd(token: string, channelId: number) {
  const channelIndex = getChannelIndex(channelId);
  const user = getUserByToken(token);
  const data = getData();
  const standUp = data.channels[channelIndex].standUp;

  if (user === undefined) {
    throw HTTPError(403, 'Token is invalid');
  }

  if (standUp.standUpMessage.length === 0) {
    standUp.standUpActive = false;
    standUp.standUpLength = 0;
    standUp.standUpMessage = [];
    setData(data);
    return;
  }

  let message = '';
  const messageLength = standUp.standUpMessage.length;
  for (let i = 0; i < messageLength; i++) {
    const messageSender = standUp.standUpMessage[i].uId;
    const user = findUser(messageSender);
    const messageSenderHandle = user.handleStr;
    const messageContent = standUp.standUpMessage[i].message;

    // join the message together
    message += `${messageSenderHandle}: ${messageContent}\n`;

    // if the message is the last message, delete the last new line
    if (i === messageLength - 1) {
      message = message.slice(0, -1);
    }
  }
  // add new lines in between messages

  const newMessage = {
    messageId: Math.floor(Math.random() * 1000000),
    uId: user.authUserId,
    message: message,
    timeSent: Math.floor(Date.now() / 1000),
    isPinned: false,
    reacts: [
      {
        reactId: 1,
        uIds: [] as number[],
        isThisUserReacted: false,
      },
    ],
  };

  data.channels[channelIndex].messages.push(newMessage);

  standUp.standUpActive = false;
  standUp.standUpLength = 0;
  standUp.standUpMessage = [];
  standUp.standUpOwner = -1;

  setData(data);

  incrementMessageStat(user.authUserId);
}

import { getData, setData } from './dataStore';
import {
  findChannel,
  findChannelByMessageId,
  findDMbyId,
  findDMbyMessageId,
  findMessageIndexInChannel,
  findMessageIndexInDM,
  findUserIndex,
  getAllMemberIds,
  getAllOwnerIds,
  getChannelIndex,
  getDmIndex,
  getUserByToken,
  incrementMessageStat,
  sendNotifChannel,
  sendNotifDm,
} from './functionHelper';
import HTTPError from 'http-errors';
import {
  messages,
  messagesObject,
  newMessageReturn,
  reactsObject,
} from './interfaces';

/**
 *
 * @param {string} token - The token of the user sending the message.
 * @param {number} channelId - The ID of the channel to send the message to.
 * @param {string} message - The message to be sent.
 * @returns {} - An object containing either an error message or the ID of the sent message.
 */
export function messageSendV1(
  token: string,
  channelId: number,
  message: string
): newMessageReturn {
  const data = getData();
  const user = getUserByToken(token);
  const channel = findChannel(channelId);
  const allMemberIds = getAllMemberIds(channel);

  // Error Checking
  if (user === undefined) {
    throw HTTPError(403, 'Token is invalid');
  }
  if (channel === undefined) {
    throw HTTPError(400, 'Channel does not exist');
  }
  if (allMemberIds.includes(user.authUserId) === false) {
    throw HTTPError(403, 'User is not registered in channel');
  }
  if (message.length < 1) {
    throw HTTPError(400, 'Message is too short');
  }
  if (message.length > 1000) {
    throw HTTPError(400, 'Message is too long');
  }

  const channelIndex = getChannelIndex(channelId);
  const messageId = Math.floor(Math.random() * 1000000);
  const newMessage = {
    messageId: messageId,
    uId: user.authUserId,
    message: message,
    timeSent: Math.floor(Date.now() / 1000),
    isPinned: false,
    reacts: [] as reactsObject[],
  };
  data.channels[channelIndex].messages.push(newMessage);

  setData(data);

  incrementMessageStat(user.authUserId);
  sendNotifChannel(user, message, allMemberIds, channel);
  return { messageId: messageId };
}
/**
 *
 * @param {string} token - The token of the user removing the message.
 * @param {number} messageId - The ID of the message to be removed.
 * @returns {} - An object containing either an error message or nothing.
 */
export function messageRemoveV1(
  token: string,
  messageId: number
): Record<string, never> {
  const data = getData();
  const user = getUserByToken(token);
  const channel = findChannelByMessageId(messageId);
  const dm = findDMbyMessageId(messageId);
  let messageIndex = -1;
  let flags;

  if (user === undefined) {
    throw HTTPError(403, 'Token is invalid');
  }

  if (channel === undefined && dm === undefined) {
    throw HTTPError(400, 'Message does not exist');
  } else if (channel !== undefined) {
    messageIndex = findMessageIndexInChannel(channel, messageId);
    flags = channel;
  } else {
    messageIndex = findMessageIndexInDM(dm, messageId);
    flags = dm;
  }

  const allMemberIds = getAllMemberIds(flags);
  const allOwnerIds = getAllOwnerIds(flags);

  if (allMemberIds.includes(user.authUserId) === false) {
    throw HTTPError(403, 'User is not registered in channel');
  }

  if (flags === channel) {
    const channelIndex = getChannelIndex(channel.channelId);
    const messageToEdit = data.channels[channelIndex].messages[messageIndex];

    if (
      messageToEdit.uId !== user.authUserId &&
      allOwnerIds.includes(user.authUserId) === false &&
      user.isGlobalOwner === 2
    ) {
      throw HTTPError(
        403,
        'User is not the author of the message and not an owner'
      );
    }

    data.channels[channelIndex].messages.splice(messageIndex, 1);
    setData(data);
    return {};
  } else {
    const dmIndex = getDmIndex(dm.dmId);
    const messageToEdit = data.dm[dmIndex].messages[messageIndex];

    if (
      messageToEdit.uId !== user.authUserId &&
      allOwnerIds.includes(user.authUserId) === false
    ) {
      throw HTTPError(403, 'User is not the author of the message');
    }

    data.dm[dmIndex].messages.splice(messageIndex, 1);
    setData(data);
    return {};

    // reacted message: "{Users handle} reacted to your message in {channel/DM name}"
    // added to a channel/DM: "{Users handle} added you to {channel/DM name}"
  }
}

/**
 *
 * @param {string} token - The token of the user editing the message.
 * @param {number} messageId - The ID of the message to be edited.
 * @param {string} message - The new message.
 * @returns {} - An object containing either an error message or nothing.
 */
export function messageEditV1(
  token: string,
  messageId: number,
  message: string
): Record<string, never> {
  const data = getData();
  const user = getUserByToken(token);
  const channel = findChannelByMessageId(messageId);
  const dm = findDMbyMessageId(messageId);
  let messageIndex = -1;
  let flags;

  if (user === undefined) {
    throw HTTPError(403, 'Token is invalid');
  }

  if (channel === undefined && dm === undefined) {
    throw HTTPError(400, 'Message does not exist');
  } else if (channel !== undefined) {
    messageIndex = findMessageIndexInChannel(channel, messageId);
    flags = channel;
  } else {
    messageIndex = findMessageIndexInDM(dm, messageId);
    flags = dm;
  }

  const allMemberIds = getAllMemberIds(flags);
  const allOwnerIds = getAllOwnerIds(flags);

  if (allMemberIds.includes(user.authUserId) === false) {
    throw HTTPError(403, 'User is not registered in channel');
  }

  if (flags === channel) {
    const channelIndex = getChannelIndex(channel.channelId);
    const messageToEdit = data.channels[channelIndex].messages[messageIndex];

    if (
      messageToEdit.uId !== user.authUserId &&
      allOwnerIds.includes(user.authUserId) === false &&
      user.isGlobalOwner === 2
    ) {
      throw HTTPError(
        403,
        'User is not the author of the message and not an owner'
      );
    }

    // Delets message if message is empty
    if (message.length < 1) {
      data.channels[channelIndex].messages.splice(messageIndex, 1);
      setData(data);
      return {};
    }
    if (message.length > 1000) {
      throw HTTPError(400, 'Message is too long');
    }

    data.channels[channelIndex].messages[messageIndex].message = message;

    setData(data);

    sendNotifChannel(user, message, allMemberIds, channel);
    return {};
  } else {
    const dmIndex = getDmIndex(dm.dmId);
    const messageToEdit = data.dm[dmIndex].messages[messageIndex];

    if (
      messageToEdit.uId !== user.authUserId &&
      allOwnerIds.includes(user.authUserId) === false
    ) {
      throw HTTPError(403, 'User is not the author of the message');
    }

    // Delets message if message is empty
    if (message.length < 1) {
      data.dm[dmIndex].messages.splice(messageIndex, 1);
      setData(data);
      return {};
    }
    if (message.length > 1000) {
      throw HTTPError(400, 'Message is too long');
    }

    data.dm[dmIndex].messages[messageIndex].message = message;
    setData(data);

    sendNotifDm(user, message, allMemberIds, dm);
    return {};
  }
}

